import numpy as np
import matplotlib.pyplot as plt
from scipy.optimize import minimize
from collections import defaultdict
from GetUserData import *


def select_bulbs(bulbs_dict, target_list, max_bulbs=10, min_decrease=1e-5, min_coeff=0.01):
    # Подготовка данных
    target = np.array(target_list)
    bulbs = {}
    for name, (spectrum, price) in bulbs_dict.items():
        bulbs[name] = (np.array(spectrum), float(price))

    # Инициализация
    residual = target.copy()
    available = list(bulbs.keys())
    selected = []
    n_bulbs = 0

    # Жадный выбор ламп
    while available and n_bulbs < max_bulbs:
        best_gain = -np.inf
        best_lamp = None
        best_a = 0.0
        best_decrease = 0.0

        for name in available:
            s, price = bulbs[name]
            dot = residual.dot(s)
            norm_sq = s.dot(s)
            if norm_sq < 1e-10:
                continue
            a = max(0.0, min(1.0, dot / norm_sq))
            if a == 0:
                continue
            decrease = 2 * a * dot - a ** 2 * norm_sq
            if decrease < 0:
                continue
            gain = decrease / (price * a) if price > 0 else decrease

            if gain > best_gain:
                best_gain = gain
                best_lamp = name
                best_a = a
                best_decrease = decrease

        if best_gain == -np.inf or best_decrease < min_decrease:
            break

        # Обновление данных
        s_best, _ = bulbs[best_lamp]
        residual -= best_a * s_best
        available.remove(best_lamp)
        selected.append(best_lamp)
        n_bulbs += 1

    # Финальная оптимизация коэффициентов
    def objective(coeffs):
        total_spec = np.zeros_like(target)
        for i, name in enumerate(selected):
            s, _ = bulbs[name]
            total_spec += coeffs[i] * s
        return np.sum((total_spec - target) ** 2)

    if selected:
        # Начальное приближение
        x0 = np.ones(len(selected)) * 0.5
        # Границы [0, 1] для каждого коэффициента
        bounds = [(0, 1)] * len(selected)
        # Минимизация функции ошибки
        result = minimize(objective, x0, bounds=bounds)
        opt_coeffs = result.x
    else:
        opt_coeffs = []

    # Формирование результата
    result_coeffs = defaultdict(float)
    for name in bulbs_dict:
        result_coeffs[name] = 0.0

    # Учет порога для коэффициентов
    final_selected = []
    final_coeffs = []
    for i, name in enumerate(selected):
        if opt_coeffs[i] >= min_coeff:
            final_selected.append(name)
            final_coeffs.append(opt_coeffs[i])
            result_coeffs[name] = opt_coeffs[i]

    # Повторная оптимизация без малых коэффициентов
    if final_selected:
        def final_objective(coeffs):
            total_spec = np.zeros_like(target)
            for i, name in enumerate(final_selected):
                s, _ = bulbs[name]
                total_spec += coeffs[i] * s
            return np.sum((total_spec - target) ** 2)

        x0 = np.array(final_coeffs)
        bounds = [(0, 1)] * len(final_selected)
        result = minimize(final_objective, x0, bounds=bounds)
        for i, name in enumerate(final_selected):
            result_coeffs[name] = result.x[i]

    return dict(result_coeffs)


# Функция для расчета суммарного спектра
def calculate_spectrum(bulbs_dict, coeffs):
    total = np.zeros(721)
    for name, coeff in coeffs.items():
        if coeff > 0:
            spectrum = bulbs_dict[name][0]
            total += coeff * np.array(spectrum)
    return total


# Функция для расчета отклонения
def calculate_deviation(target, fitted):
    # Используем относительную ошибку L1
    abs_diff = np.abs(target - fitted)
    relative_error = np.where(target > 0, abs_diff / target, 0)
    return np.mean(relative_error) * 100  # Среднее отклонение в %


# Функция построения графика
def plot_spectrum_comparison(target, fitted, deviation):
    wavelengths = np.linspace(380, 1100, 721)

    plt.figure(figsize=(12, 6))
    plt.plot(wavelengths, target, 'b-', linewidth=2, label='Целевой спектр')
    plt.plot(wavelengths, fitted, 'r--', linewidth=1.5,
             label=f'Аппроксимация (отклонение: {deviation:.2f}%)')

    # Заливка областей расхождения
    plt.fill_between(wavelengths, target, fitted, where=fitted > target,
                     color='red', alpha=0.1, label='Избыток интенсивности')
    plt.fill_between(wavelengths, target, fitted, where=fitted < target,
                     color='blue', alpha=0.1, label='Недостаток интенсивности')

    plt.title('Сравнение целевого и аппроксимированного спектров', fontsize=14)
    plt.xlabel('Длина волны (нм)', fontsize=12)
    plt.ylabel('Интенсивность', fontsize=12)
    plt.grid(alpha=0.3)
    plt.legend(fontsize=10)
    plt.tight_layout()

    # Сохранение и отображение
    plt.savefig('spectrum_comparison.png', dpi=300)
    plt.show()


